import React, { useState, useEffect } from 'react';
import { Modal, Button, Table } from 'react-bootstrap';
import axios from 'axios';

const HistoryModal = ({ show, onHide }) => {
  const [historyData, setHistoryData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('https://thai-card.onrender.com/api/citizen/');
        setHistoryData(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching history data:', error.message);
        setLoading(false);
      }
    };

    if (show) {
      fetchData();
    }
  }, [show]);

  return (
    <Modal
    show={historyModalShow}
    onHide={() => setHistoryModalShow(false)}
    size="lg" // set the size to large
    dialogClassName="custom-modal"
  >
      <Modal.Header closeButton>
        <Modal.Title>History</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {loading ? (
          <p>Loading history data...</p>
        ) : (
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>ID Number</th>
                <th>Name</th>
                <th>Last Name</th>
                <th>Date of Birth</th>
                <th>Date of Issue</th>
                <th>Date of Expiry</th>
              </tr>
            </thead>
            <tbody>
              {historyData.map((item) => (
                <tr key={item._id}>
                  <td>{item.identification_number}</td>
                  <td>{item.name}</td>
                  <td>{item.last_name}</td>
                  <td>{item.date_of_birth}</td>
                  <td>{item.date_of_issue}</td>
                  <td>{item.date_of_expiry}</td>
                  {/* Add more cells as needed */}
                </tr>
              ))}
            </tbody>
          </Table>
        )}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default HistoryModal;
