import React, { useState } from "react";
import { Button, Image } from "react-bootstrap";
import MyVerticallyCenteredModal from "../components/Modal";
import EditModal from "../components/EditModal";
import axios from "axios";
import MyModal from "../components/History";
import Img from "../assets/img/hero-img.png";
const Home = () => {
  const [modalShow, setModalShow] = useState(false);
  const [image, setImage] = useState();
  const [isImageUploaded, setIsImageUploaded] = useState(null);
  const [name, setName] = useState("name");
  const [idNumber, setIdNumber] = useState();
  const [identification_number, setIdentification_number] = useState();
  const [last_name, setLastName] = useState("lastname");
  const [date_of_birth, setDOB] = useState("date of birth");
  const [date_of_issue, setDOI] = useState("date of issue");
  const [date_of_expiry, setDOE] = useState("date of expiry");
  const [imageloading, setImageLoading] = useState(false);
  const [isFindingID, setisFindingID] = useState(false);
  const [modalEdit, setModalEditShow] = useState(false);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const handleUpdateID = () => {
    setModalEditShow(true);
    setisFindingID(false);
  };

  const handleSave = async () => {
    setIsImageUploaded(false);
    const response = await axios.post(
      "https://thai-card.onrender.com/api/citizen",
      {
        idNumber,
        name,
        last_name,
        date_of_birth,
        date_of_issue,
        date_of_expiry,
      }
    );
    setIdNumber("");
  };
  // const handleUploadID = () => {
  //   const inputElement = document.createElement("input");
  //   inputElement.type = "file";
  //   inputElement.accept = "image/jpeg"; // Accept only JPEG and PNG files

  //   inputElement.onchange = (event) => {
  //     const selectedFile = event.target.files[0];

  //     // Check if a file is selected
  //     if (!selectedFile) {
  //       setError("Please select a valid image file.");
  //       return;
  //     }

  //     // Check if the file is in JPEG or PNG format
  //     if (!["image/jpeg"].includes(selectedFile.type)) {
  //       setError("Supported formats: JPEG, PNG.");
  //       return;
  //     }

  //     // Check if the file size is less than or equal to 2MB
  //     if (selectedFile.size > 2 * 1024 * 1024) {
  //       setError("Maximum file size: 2MB.");
  //       return;
  //     }

  //     // Clear any previous errors
  //     setError(null);
  //     setImage(selectedFile);
  //     // setIsImageUploaded(true);
  //   };
  //   inputElement.click();
  //   setModalShow(true);
  //   setisFindingID(false);
  // };
  const handleUploadID = () => {
    const inputElement = document.createElement("input");
    inputElement.type = "file";
    inputElement.accept = "image/jpeg"; // Accept only JPEG files
  
    inputElement.onchange = (event) => {
      const selectedFile = event.target.files[0];
  
      // Check if a file is selected
      if (!selectedFile) {
        setError("Please select a valid image file.");
        return;
      }
  
      // Check if the file is in JPEG format
      if (!["image/jpeg"].includes(selectedFile.type)) {
        setError("Supported format: JPEG.");
        return;
      }
  
      // Check if the file size is less than or equal to 2MB
      if (selectedFile.size > 2 * 1024 * 1024) {
        setError("Maximum file size: 2MB.");
        return;
      }
  
      // Clear any previous errors
      setError(null);
      setImage(selectedFile);
      // setIsImageUploaded(true);
    };
  
    // Trigger the file upload dialog when the button is clicked
    
      inputElement.click();
      setModalShow(true);
      setisFindingID(false);
    
  
    // Render a button that triggers the file upload dialog
    return (
      <button onClick={handleClick}>
        Upload ID
      </button>
    );
  };
  

  const handleHistory = () => {
    setModalShow(true);
  };
  const handleCancel = () => {
    setIsImageUploaded(false);
    setisFindingID(false);
    setIdNumber("");
  };

  const handleDelete = async () => {
    setIsImageUploaded(false);
    setisFindingID(false);

    console.log("IN DELETE CALL", idNumber);
    console.log(idNumber);
    try {
      const response = await axios.delete(
        `https://thai-card.onrender.com/api/citizen/${idNumber}`
      );
      setIdNumber("");
    } catch (error) {
      console.error("Error during data deletion:", error.message);
    }
  };

  const handleFind = async () => {
    try {
      const response = await axios.get(
        `https://thai-card.onrender.com/api/citizen/${idNumber}`
      );

      const {
        name,
        identification_number,
        last_name,
        date_of_birth,
        date_of_issue,
        date_of_expiry,
      } = response.data;
      setIdentification_number(identification_number);
      setDOB(date_of_birth);
      setDOE(date_of_expiry);
      setName(name);
      setLastName(last_name);
      setDOI(date_of_issue);
      setisFindingID(true);
      setError(null);
    } catch (error) {
      console.error("Error during data retrieval:", error.message);
      setError(null);
    }
  };

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <>
      <section id="hero" className="d-flex align-items-center">
        <div className="container">
          <div className="row">
            <div
              className="col-lg-6 d-flex flex-column justify-content-center pt-4 pt-lg-0 order-2 order-lg-1"
              data-aos="fade-up"
              data-aos-delay="200"
            >
              <h1>Better Solutions For Your Business</h1>
              <h2>
                We are team of talented designers making websites with Bootstrap
              </h2>
              <div className="d-flex justify-content-center justify-content-lg-start">
                <Button variant="light" onClick={handleUploadID}>
                  Upload ID Card
                </Button>
                <p
                  onClick={handleOpenModal}
                  className="glightbox btn-watch-video"
                >
                  <i className="bi bi-play-circle"></i>
                  <span>History</span>
                </p>
              </div>
            </div>
            <div
              className="col-lg-6 order-1 order-lg-2 hero-img"
              data-aos="zoom-in"
              data-aos-delay="200"
            >
              <img src={Img} className="img-fluid animated" alt="" />
            </div>
          </div>
        </div>
      </section>

      <section id="about" className="about">
        <div className="container" data-aos="fade-up">
          <div className="section-title">
            {isImageUploaded && !imageloading ? <h2>Card Details</h2> : null}
          </div>

          <div className="row content">
            <div className="col-lg-6">
              {isImageUploaded && !imageloading ? (
                <div div className="d-flex flex-column align-items-center">
                  <Image src={image} thumbnail style={{ maxHeight: "200px" }} />
                </div>
              ) : // The third case can be left empty or filled with another component or text
              null}
            </div>
            <div className="col-lg-6 pt-4 pt-lg-0">
              {!imageloading && isImageUploaded ? (
                <div className="">
                  <h6 className="my-2">Name: {name}</h6>
                  <h6 className="my-2">Last Name: {last_name}</h6>
                  <h6 className="my-2">ID Number: {idNumber}</h6>
                  <h6 className="my-2">Date Of Birth: {date_of_birth}</h6>
                  <h6 className="my-2">Date Of Issue: {date_of_issue}</h6>
                  <h6 className="my-2">Date Of Expiry: {date_of_expiry}</h6>
                  <div className="my-3 p-3 ">
                    <Button
                      className="mt-3 ml-2"
                      variant="success"
                      onClick={handleSave}
                    >
                      Save
                    </Button>

                    <Button
                      className="mt-3  mx-auto mr-2"
                      variant="primary"
                      onClick={() => setIsImageUploaded(false)}
                    >
                      Cancel
                    </Button>
                  </div>
                </div>
              ) : !isImageUploaded && imageloading ? (
                <h2>Loading Details....</h2>
              ) : null}
            </div>
          </div>
        </div>
      </section>

      <div className="container bg-body-secondary ">
        <div className="form_outline">
          <MyVerticallyCenteredModal
            show={modalShow}
            onHide={() => setModalShow(false)}
            setImage={setImage}
            setIsImageUploaded={setIsImageUploaded}
            setDOB={setDOB}
            setDOE={setDOE}
            setDOI={setDOI}
            setIdNumber={setIdNumber}
            setLastName={setLastName}
            setName={setName}
            setImageLoading={setImageLoading}
          />

          <EditModal
            show={modalEdit}
            onHide={() => setModalEditShow(false)}
            idNumber={idNumber}
            setIdNumber={setIdNumber}
          />
          <footer id="footer">
            <div class="footer-newsletter">
              <div class="container">
                <div class="row justify-content-center">
                  <div class="col-lg-6">
                    <h4>Search Details Via ID Number</h4>
                    <p>
                      Tamen quem nulla quae legam multos aute sint culpa legam
                      noster magna
                    </p>
                    <input
                      type="text"
                      value={idNumber}
                      onChange={(e) => setIdNumber(e.target.value)}
                      name="ID Number"
                    />
                    <button
                      type="submit"
                      className="btn btn-secondary border-lg rounded-lg"
                      onClick={handleFind}
                      value="Search"
                    >
                      Search{" "}
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </footer>

          {isFindingID ? (
            <div className="d-flex flex-column align-items-center">
              <h6 className="my-2">IdNumber: {identification_number}</h6>
              <h6 className="my-2">Name: {name}</h6>
              <h6 className="my-2">Last Name: {last_name}</h6>
              <h6 className="my-2">Date Of Birth: {date_of_birth}</h6>
              <h6 className="my-2">Date Of Issue: {date_of_issue}</h6>
              <h6 className="my-2">Date Of Expiry: {date_of_expiry}</h6>
              <div className="my-3">
                <Button
                  className="mt-3 ml-2"
                  variant="success"
                  onClick={handleDelete}
                >
                  Delete ID
                </Button>
                <Button
                  className="mt-3 mr-2"
                  variant="primary"
                  onClick={handleCancel}
                >
                  Cancel
                </Button>
                <Button
                  className="mt-3 ml-2"
                  variant="warning"
                  onClick={handleUpdateID}
                >
                  Edit
                </Button>
              </div>
            </div>
          ) : null}
        </div>
      </div>
    </>
  );
};

export default Home;
